contracts/TamagoSan.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;
import "./ERC721A.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./Traits.sol";
import "./Tsan.sol";

contract TamagoSan is ERC721A,Ownable,AccessControl,ERC1155Holder{

    using Strings for uint256;
    //Traits
    //add trait contract address
    Traits public traits = Traits(address(0xAbE68a0442Cf4eed794820118e4F3fFDF619D29F));
    Tsan public tsan = Tsan(address(0x765138B367747cA9EF2Eaf46f13E67877D940632));
    //base Price 0.025 MATIC
    uint public basePrice = 0.00025 ether;
    //Max Supply
    uint maxSupply = 9999;
    uint  public FREE_MINT           = 2;
    uint  public MAX_PER_WALLET      = 200;
    uint  public MAX_FREE_PER_WALLET = 1;
    uint  public freeMinted   = 0;
    mapping(address => bool) private freeMintByWallet;
    mapping(address => uint) private nftByWallet;
    //MINTER
    bytes32 MINTER_ROLE = keccak256("MINTER_ROLE");
    //
    //Stake mapping
    mapping(uint256=>uint256[]) stakedTraits;

    string baseURI =  "https://tamagosan.fra1.digitaloceanspaces.com/tamagosanMetadata/";


    constructor() ERC721A("Tamagosan NFT","TAMAGO"){
        //add signer public key (same as in django project)
        _setupRole(MINTER_ROLE, address(0x396E1d4d0Dc9e86Eff33f2cF9fe4F6F2f2FB1164));
    }

    // function mint() public{
    //     _mint(msg.sender,totalSupply()+1);
    // }

    function randomMint(address to, uint256[] memory tokenIDs,bytes32 messageHash,bytes calldata signature, uint _quantity) external payable {
        uint price = basePrice;
        require(msg.value >= price * _quantity, "Not enough Funds!");
        require(_quantity <= MAX_PER_WALLET, "Max 6 NFTs per wallet (2 free NFTs Max include)");
        require(totalSupply() + _quantity <= maxSupply, "All the NFTs were sold");
        verifyHash(to, tokenIDs, messageHash);
        verifySigner(messageHash,signature);
        for(uint i=0;i<tokenIDs.length;i++){
            traits.mint(msg.sender,tokenIDs[i]);
        }
        _safeMint(msg.sender, _quantity);
        tsan.mint(msg.sender, 1000*10**18*_quantity);
        nftByWallet[msg.sender] += _quantity;
    }

    function randomFreeMint(address to, uint256[] memory tokenIDs,bytes32 messageHash,bytes calldata signature, uint _quantity) external {
        require(freeMinted < FREE_MINT && !freeMintByWallet[msg.sender], "freeMint is available just one time and for 2 NFTs max");
        require(nftByWallet[msg.sender] >= 2, "You have not buy 2 NFTs for claim your free");
        verifyHash(to, tokenIDs, messageHash);
        verifySigner(messageHash,signature);
        for(uint i=0;i<tokenIDs.length;i++){
            traits.mint(msg.sender,tokenIDs[i]);
        }
        _safeMint(msg.sender, _quantity);
        tsan.mint(msg.sender, 1000*10**18*_quantity);
        freeMinted++;
        nftByWallet[msg.sender] += _quantity;
        freeMintByWallet[msg.sender] = true;
    }

    function randomFreeMint2(address to, uint256[] memory tokenIDs,bytes32 messageHash,bytes calldata signature, uint _quantity) external {
        require(freeMinted < FREE_MINT && !freeMintByWallet[msg.sender], "freeMint is available just one time and for 2 NFTs max");
        require(nftByWallet[msg.sender] >= 4, "You have not buy 4 NFTs for claim your 2 free");
        verifyHash(to, tokenIDs, messageHash);
        verifySigner(messageHash,signature);
        for(uint i=0;i<tokenIDs.length;i++){
            traits.mint(msg.sender,tokenIDs[i]);
        }
        _safeMint(msg.sender, _quantity);
        tsan.mint(msg.sender, 1000*10**18*_quantity);
        freeMinted++;
        nftByWallet[msg.sender] += _quantity;
        if (nftByWallet[msg.sender] == 6) {
            freeMintByWallet[msg.sender] = true;
        }
    }

    function verifySigner(bytes32 messageHash,bytes calldata signature) internal view{
        uint8 v;
        bytes32 r;
        bytes32 s;
        (v,r,s) = splitSignature(signature);
        address signer = ecrecover(messageHash, v, r, s);
        require(hasRole(MINTER_ROLE, signer),"Invalid Signer");
    }

    function splitSignature(bytes calldata signature) public pure returns(uint8 v,bytes32 r,bytes32 s){
        r = bytes32(signature[:32]);
        s = bytes32(signature[32:64]);
        v = uint8(bytes1(signature[64:]));
        return (v,r,s);
    }

    function verifyHash(address to,uint256[] memory tokenIDs,bytes32 messageHash) internal pure{
        bytes32 hash = generateHash(to,tokenIDs);
        require(hash == messageHash,"Invalid messageHash");
    }

    function generateHash(address to,uint256[] memory tokenIDs) internal pure returns(bytes32){
        bytes32 hash = keccak256(abi.encodePacked(to,tokenIDs));
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32",hash));
    }

    function ownerMint() public onlyOwner{
        _mint(msg.sender,totalSupply()+1);
    }

    function withdraw(address to) public onlyOwner{
        require(address(this).balance>0,"No balance to withdraw!");
        payable(to).transfer(address(this).balance);
    }

    function hasFreeMint(address _address) public view returns(bool) {
        return freeMintByWallet[_address] ? true : false;
    }

    function setPrice(uint256 price) public onlyOwner{
        basePrice = price;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721A, AccessControl,ERC1155Receiver) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    //staking logic
    function stakeParts(uint256 tamagosanID,uint256[] calldata tokenIDs,uint256[] calldata stakeIDs,uint256[] calldata amounts) public{
        require(ownerOf(tamagosanID)==msg.sender,"This NFT is not owned by you!");
        traits.safeBatchTransferFrom(msg.sender,address(this),stakeIDs,amounts,"");
        stakedTraits[tamagosanID] = tokenIDs;
    }

    function getOwnedNftIDs() public view returns(uint256[] memory){
        uint256 size = balanceOf(msg.sender);
        uint[] memory nfts = new uint[](size);
        uint index = 0;
        for(uint i=1;i<=totalSupply();i++){
            if(ownerOf(i)==msg.sender){
                nfts[index] = i;
                index+=1;
            }
        }   
        return nfts;
    }

    function getStakedTraits(uint256 tamagosanID) public view returns(uint256[] memory){
        return stakedTraits[tamagosanID];
    } 

    function unstakeParts(uint256 tamagosanID,uint256[] calldata tokenIDs,uint256[] calldata unstakeIDs,uint256[] calldata unstakeAmounts) public{
        require(ownerOf(tamagosanID)==msg.sender,"This NFT is not owned by you!");
        traits.safeBatchTransferFrom(address(this),msg.sender,unstakeIDs,unstakeAmounts,"");
        stakedTraits[tamagosanID] = tokenIDs;
    }

    function tokenURI(uint _tokenId) public view virtual override returns (string memory) {
        require(_exists(_tokenId), "URI query for nonexistent token");

        return string(abi.encodePacked(baseURI, _tokenId.toString(), ".json"));
    }

}

contracts/Traits.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;
import '@openzeppelin/contracts/token/ERC1155/ERC1155.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Strings.sol';

contract Traits is ERC1155,Ownable{

    using Strings for uint256;

    string _baseURI = "https://tamagosan-server-cmh4q.ondigitalocean.app/metadataTrait/";

    constructor() ERC1155(""){
    }

    function mint(address to,uint256 tokenID) public{
        _mint(to, tokenID, 1,'');
    }

    function uri(uint256 tokenID) public view virtual override returns (string memory) {
        return string(abi.encodePacked(_baseURI,tokenID.toString()));
    }

}

contracts/Tsan.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Tsan is ERC20, Ownable {

    mapping(address => bool) admins;

    constructor() ERC20("Tsan Token", "$TSAN") {}

    function mint(address _to, uint _amount) external {
        require(admins[msg.sender], "Cannot mint if not admin");
        _mint(_to, _amount);
    }

    function addAdmin(address _admin) external onlyOwner {
        admins[_admin] = true;
    }

    function removeAdmin(address _admin) external onlyOwner {
        admins[_admin] = false;
    }
}

